<?php/** * @license GNU/GPL, see LICENSE.php * @author Jan Linhart * @link http://escope.cz */// no direct accessdefined( '_JEXEC' ) or die( 'Restricted access' );   $conuter = 1;//echo '<pre>';var_dump($datas);?>                 <div class="pg_data"><?phpforeach($datas as $data){$txt = '';if($data->amount > 0) {    $catLink = JRoute::_('index.php?option=com_phocagallery&view=category&id='.$data->cat_id.':'.$data->alias);    if($limitImages > 0){        foreach($data->images as $image){            $path = explode("/",$image->filename);            $pathLength = count($path);            $imageName = $path[$pathLength - 1];            $path[$pathLength - 1] = "thumbs/phoca_thumb_".$thumbnail."_".$imageName;            $path2 = implode("/",$path);            // for picasa images            if ($image->extl != '' && $image->extl != null) {                if ($thumbnail == 'l') {$imgLink = $image->extl;}                if ($thumbnail == 'm') {$imgLink = $image->extm;}                if ($thumbnail == 's') {$imgLink = $image->exts;}            }            // for phoca images            else {$imgLink = 'images/phocagallery/'.$path2;}            $txt .= '<a class="image" href="'.$catLink.'">';            $txt .= '<img class="img_round" src="'.$imgLink.'" alt="'.$data->cat_title.'" title="'.$data->cat_title.'" />';            $txt .= '</a>';        }    }?>                                                                             <div class="one_item one_item<?php echo $conuter; ?>">  <?php echo $txt; ?>  <h4>  	<?php if($showCatname == 1):?>    <a href="<?php echo $catLink; ?>"><?php echo $data->cat_title; ?></a>    <?php endif;?>    <?php if($countImages) { ?>    <span class="image_count">(<?php echo $data->amount; ?>)</span>    <?php } ?>   </h4>  <?php if($showDate) { ?>  <p class="comments_count"><?php echo JFactory::getDate($data->cdate)->toFormat($dateFormat); ?></p>  <?php } ?>  <?php if($countComments && $data->comments > 0) { ?>  <p class="comments_count"><?php echo str_replace('[number]', $data->comments, $commentsDesc); ?></p>  <?php } ?>  <?php if($countHits && $data->hits > 0) { ?>  <p class="comments_count"><?php echo str_replace('[number]', $data->hits, $hitsDesc); ?></p>  <?php } ?>      <?php if($showReadMore) { ?>    <div class="cat_description"><?php echo strip_tags(mb_substr($data->cat_description,0,$descriptionLength)); ?>    <?php if($data->cat_description != '' && $data->cat_description != null) {echo $descriptionEnd;} ?>    <a class="read_more" href="<?php echo $catLink ?>"><?php echo $readMore; ?></a>    </div>  <?php } ?>  <div class="clr"></div>  </div><?php     $conuter++;    }}?></div><div class="clr"></div> <?php if($round) { ?>   <script type="text/javascript">       jQuery(document).ready(function(){        var size = <?php echo $borderWidth; ?>;        var color = "<?php echo $borderColor; ?>";        var radius = <?php echo $cornerRadius; ?>;        jQuery(".img_round").imgr({size:size+"px",color:color,radius:radius+"px"});       });  </script><?php } ?> 